import React, { useState, useReducer } from "react";
import Controls from "./Controls";
import Light from "./Light";
import lightData from "../lightData";
// console.log("lightData", lightData);

const lightReducer = (lightState, action) => {
  switch (action.type) {
    case "off":
      return { color: "rgba(0,0,0,1)" };
    case "low":
      return { color: "rgba(0,0,0,.6)" };
    case "medium":
      return { color: "rgba(0,0,0,.4)" };
    case "high":
      return { color: "rgba(0,0,0,0.1)" };
  }
};

function App() {
  const [lightState, dispatchLightState] = useReducer(lightReducer, {
    color: "rgba(0,0,0,1)",
  });

  const controls = ["off", "low", "medium", "high"];
  // const [color, setColor] = useState("rgba(0,0,0,1");

  // const handleClick = (props) => {
  //   setColor(props);
  //   // console.log(props);
  // };

  return (
    <div className="App">
      <Controls controls={controls} lightChange={dispatchLightState} />
      <Light color={lightState.color} />
    </div>
  );
}

export default App;
