import React, { useEffect, useState } from "react";
import Button from "./Button";

const SearchBar = () => {
  const [searchItem, setSearchItem] = useState("");
  const [display, setDisplay] = useState({
    imageUrl: "",
  });

  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchPost = async (url, signal) => {
    setIsLoading(true);
    setError(null);

    try {
      const rest = await fetch(url, { signal });

      if (rest.status !== 200) {
        throw new Error("Something went wrong.");
      }

      const data = await rest.json();
      console.log(data.data[0].images.original.url);
      setDisplay({
        imageURL: data.imageUrl,
      });
    } catch (err) {
      setError(err.message);
    }

    setIsLoading(false);
  };

  const handleSelectionChange = (event) => {
    setSearchItem(event.target.value);
  };

  useEffect(() => {
    const url = `https://api.giphy.com/v1/gifs/search?api_key=04oi8nawoY2cYVLEb7cQY8W0mbMjht8Y&q=${searchItem}&limit=1&offset=0&rating=g&lang=en`;
    const controller = new AbortController();
    fetchPost(url, controller.signal);

    return () => {
      controller.abort();
    };
  }, [searchItem]);

  const handleSubmit = (event) => {
    event.preventDefault();
  };

  //     if (task.length === 0) {
  //       setError(true);
  //     }

  //     setTask("");
  //   };

  //   const handleChange = (event) => {
  //     setTask(event.target.value);
  //   };

  //   return () => {
  //     controller.abort();
  //   };
  // }, [selection]);

  // const handleSubmit = (event) => {
  //   event.preventDefault();
  // };

  return (
    <>
      <input
        placeholder="Use me to make a random search!"
        onChange={handleSelectionChange}
        id="selection"
      ></input>
      <Button type="submit" onClick={handleSubmit}>
        Submit
      </Button>
      <div>
        Image: <img src={display} />
      </div>
      <br />
      {!isLoading && <>{/* <div>image:{display.image}</div> */}</>}
      {isLoading && <div className="centered"></div>}
      {!isLoading && error && <p>{error}</p>}
    </>
  );
};

export default SearchBar;
