import React, { useState, useEffect } from "react";
import { DropdownList } from "react-widgets/cjs";
// import { Dropdown } from "bootstrap";

const commodityList = [
  "RICE",
  "WHEAT",
  "SUGAR",
  "CORN",
  "WTIOIL",
  "BRENTOIL",
  "SOYBEAN",
  "COFFEE",
  "XAU",
  "XAG",
  "XPD",
  "XPT",
  "XRH",
  "RUBBER",
  "ETHANOL",
  "CPO",
  "NG",
];

const Input = () => {
  const [commodity, setCommodity] = useState("");

  const commodityAPI = `https://www.commodities-api.com/api/latest?access_key=jhvo01w5j98zhcbi517u32j1mc14wgkh1zwzfp8rx4x3bdzujvnvx6gzody4& base = ${commodityList}
& symbols = USD `;

  const makeApiCall = async () => {
    const res = await fetch(commodityAPI);
    const rawData = await res.json();
    console.log(rawData);
  };

  const handleSelectionChange = (event) => {
    setCommodity(event.target.value);
    console.log(event);
  };

  return (
    <div>
      <DropdownList
        data={commodityList}
        onChange={handleSelectionChange}
        // onClick={makeApiCall}
      />
    </div>
  );
};

export default Input;
