import React, { useState } from "react";
import ChickenContext from "../context/chicken-context";

const Form = (props) => {
  const chickenCtx = useContext(ChickenContext);

  const [product, setProduct] = useState({ name: "", price: "" });
  console.log("product - ", product);

  const handleSubmit = () => {
    props.handleSubmit(product);
    console.log("handleSubmit");
    setProduct({ name: "", price: "", description: "" });
  };

  const handleChange = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    console.log("handleChange - event", event.target.computedName);
    console.log("handleChange - value", event.target.value);
    setProduct({
      ...product,
      [name]: value,
    });
  };

  return (
    <div className="wrapper">
      <div className="form-signin">
        <h2 className="form-signin-heading">{props.title}</h2>
        <input
          onChange={() => handleChange(event)}
          value={product.name}
          type="text"
          className="form-control"
          name="name"
          placeholder="product name"
        />
        <input
          onChange={() => handleChange(event)}
          value={product.price}
          type="text"
          className="form-control"
          name="price"
          placeholder="price"
        />
        <input
          onChange={() => handleChange(event)}
          value={product.description}
          type="text"
          className="form-control"
          name="description"
          placeholder="description"
        />
        <button
          className="btn btn-lg btn-primary btn-block"
          onClick={() => handleSubmit()}
        >
          Submit
        </button>
      </div>
    </div>
  );
};

export default Form;
