import React, { useReducer } from "react";

const lightReducer = (lightState, action) => {
  switch (action.type) {
    case "off":
      return { color: "rgba (0,0,0,0)" };
    case "low":
      return { color: "rgba(0,0,0,.4)" };
    case "med":
      return { color: "rgba(0,0,0,.6)" };
    case "high":
      return { color: "rgba(0,0,0,1)" };
  }
};

function Controls(props) {
  const [lightState, dispatchLightState] = useReducer(lightReducer, {
    color: "rgba(0,0,0,1)",
  });

  const handleClick = (button) => {
    console.log("Controls - handleClick - button", button);
    dispatchLightState({ type: button });
  };
  //props.controls = ["off", "low", "med", "high"]
  //"off" iteration d="off" i = 0
  //<button key=0 onClick=handleClick("off")>
  const lightButtons = props.controls.map((d, i) => {
    return (
      <button key={i} onClick={() => handleClick(d)}>
        {d}
      </button>
    );
  });

  return (
    <>
      <div className="controls">{lightButtons}</div>
      <p>{lightState}</p>
    </>
  );
}

export default Controls;
