import React, { useContext } from "react";
import ChickenContext from "../context/chicken-context";

function AllTheThings() {
  console.log("this is props  from ALlTheThings", props);

  const chickenCtx = useContext(ChickenContext);

  let things = chickenCtx.products.map((d, i) => {
    return (
      <li key={i} onClick={() => chickenCtx.handleClick(d)}>
        {d.name} - ${d.price}
        <br></br>
        {d.description}
      </li>
    );
  });
  return (
    <div className="AllTheThings">
      <h2>Put these in your cart!</h2>
      {things}
    </div>
  );
}

export default AllTheThings;
