import React, { useState, useEffect } from "react";

//Child

const AddNewItem = (props) => {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [validPrice, setValidPrice] = useState(false);
  const [text, enableButton] = useState(true);
  // const [validName, setValidName] = useState(false);

  // const handleTextChange = (event) => {
  //   enableButton(event.target.value);
  // };

  const handlePriceChange = (event) => {
    setPrice(event.target.value);
    // enableButton(true);
  };

  const handleNameChange = (event) => {
    setName(event.target.value);
    // enableButton(event.target.value);
  };

  const handleDescriptionChange = (event) => {
    setDescription(event.target.value);
    // enableButton(event.target.value);
  };

  // const changeHandler = () => {
  //   if (){}
  // }

  useEffect(() => {
    // price validation
    // setPrice(price.match(/^[0-9]+$/) != null);
    if (isNaN(price)) {
      alert("Please use numbers!");
    } else {
      setValidPrice(true);
    }
  }, [price]);

  useEffect(() => {
    if (name.length && price.length && description.length) {
      alert("Please fill in all inputs!");
    } else {
      enableButton(true);
    }
  }, [price, name, description]);

  const handleSubmit = (event) => {
    event.preventDefault();
    const newProduct = {
      name,
      price,
      description,
    };
    props.handleClick(newProduct);

    if (validPrice) {
      //// validation?
      setValidPrice(setPrice);
      // enableButton(true);
    }

    setName("");
    setPrice("");
    setDescription("");
  };

  return (
    <>
      <input
        value={price}
        id="price-input"
        type="text"
        onChange={handlePriceChange}
        placeholder="Input price here"
      ></input>

      <input
        value={name}
        id="name-input"
        type="text"
        onChange={handleNameChange}
        placeholder="Input name here"
      ></input>

      <input
        value={description}
        id="description-input"
        type="text"
        onChange={handleDescriptionChange}
        placeholder="Input description here"
      ></input>

      <button onClick={handleSubmit} disabled={!text}>
        Submit
      </button>
    </>
  );
};

export default AddNewItem;
