import React, { useEffect, useState } from "react";

const SearchBar = () => {
  const { searchItem, setSearchItem } = useState("");
  const [image, setImage] = useState({
    img: "",
  });

  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchPost = async (url, signal) => {
    setIsLoading(true);
    setError(null);

    try {
      const rest = await fetch(url, { signal });

      if (rest.status !== 200) {
        throw new Error("Something went wrong.");
      }

      const data = await rest.json();

      setPost({
        img: data.img,
      });
    } catch (err) {
      setError(err.message);
    }

    setIsLoading(false);
  };

  const handleSelectionChange = (event) => {
    setSelection(event.target.value);
  };

  useEffect(() => {
    const url =
      "https://api.giphy.com/v1/gifs/random?api_key=04oi8nawoY2cYVLEb7cQY8W0mbMjht8Y" +
      selection;
    const controller = new AbortController();
    fetchPost(url, controller.signal);

    return () => {
      controller.abort();
    };
  }, [selection]);

  return;
  <>
    <Input placeholder="Use me to make a random search!"></Input>
  </>;
};

export default SearchBar;
