import React, { useContext } from "react";
import ChickenContext from "../context/chicken-context";

function MyShoppingCart() {
  const chickenCtx = useContext(ChickenContext);

  //console.log("this is props  from MyShoppingCart", props)
  let things = chickenCtx.cart.map((d, i) => {
    return (
      <li key={i} onClick={() => chickenCtx.handleClick(i)}>
        {d.name} - ${d.price}
        <br></br>
        {d.description}
      </li>
    );
  });
  return (
    <div className="MyShoppingCart">
      <h2>Your Cart!</h2>
      {things}
    </div>
  );
}

export default MyShoppingCart;
